    const RollbackPopup = () => {

        const handleFetchData = () => {
            let form_arr = { 'type': 'get_wkit_version' };

            wdKit_Form_data(form_arr)
                .then((result) => {
                    if (result?.success) {
                        setWkit_versions(result?.data)
                    } else {
                        let error_msg = result?.message || 'Operation failed';
                    }
                })
        };

        /** custom drop down functionality */
        const Drop_down_toggle = (e) => {
            let main_object = e.target.closest(".wkit-wb-custom-dropDown")
            let drop_down = main_object.querySelector(".wkit-wb-custom-dropDown-content") ? main_object.querySelector(".wkit-wb-custom-dropDown-content") : "";
            if (drop_down) {
                if (Object.values(drop_down.classList).includes("wkit-wb-show")) {
                    drop_down.classList.remove("wkit-wb-show");
                    setDropdownOpen(false);
                } else {
                    drop_down.classList.add("wkit-wb-show");
                    setDropdownOpen(true);
                }
            }
        }

        const Rollback_wdkit = async () => {
            if ('Select Version' !== Select_version) {
                if (!confirm(`Are you sure you want to reinstall ${Select_version} version?`)) {
                    return;
                }
                setRollbackLoader(true);

                let obj = {
                    'type': 'rollback_wdkit',
                    'version': Select_version
                }

                await wdKit_Form_data(obj).then(async (res) => {
                    window.location.reload(true);
                })

            }
        }

        if (!Wkit_versions) {
            handleFetchData();
        }

        return (
            <>
                <div className='wkit-rollBack-container'>
                    <div className='wkit-wb-custom-dropDown' onClick={(e) => { Drop_down_toggle(e) }}>
                        <div className='wkit-wb-custom-dropDown-header'>
                            <label>{Select_version}</label>
                            <img className={DropdownOpen ? 'wkit-rotate-icon' : ''} src={img_path + 'assets/images/wb-svg/controller-open.svg'} />
                        </div>
                        <div className='wkit-wb-custom-dropDown-content'>
                            {Wkit_versions?.length > 0 ?
                                Wkit_versions.sort((a, b) => a.localeCompare(b, undefined, { numeric: true }))
                                    .map((v_name, index) => {
                                        return (
                                            <option key={index} onClick={() => {
                                                setSelect_version(v_name);
                                                BtnDisable && setBtnDisable(false);
                                            }}>{v_name}</option>
                                        );
                                    })
                                :
                                <span className='wkit-rollback-loader'>
                                    <span className='wkit-loading'></span>
                                </span>
                            }

                        </div>
                    </div>
                    {
                        RollbackLoader ?
                            <button className='wkit-pink-btn-class wkit-rollBack-btn'><span className='wkit-loading'></span></button>
                            :
                            <button className='wkit-pink-btn-class wkit-rollBack-btn' disabled={!!BtnDisable} onClick={(e) => { Rollback_wdkit(e) }}>{__('Roll Back')}</button>
                    }
                </div>
            </>
        );
    };
    /**
     * Change Event When On Off store in redux
     * 
     * @version 1.0.0
     * */
    const handleChange = (type, val) => {
        setdisableswitch(true);
        let FinalData = Object.assign({}, props?.wdkit_meta?.Setting, { [type]: val });
        let new_setting = Object.assign({}, props?.wdkit_meta, { 'Setting': FinalData });

        props.wdkit_set_meta(new_setting);

        let form_arr = { 'type': 'wkit_setting_panel', 'event': 'set', 'data': JSON.stringify(FinalData) }

        wdKit_Form_data(form_arr).then(async (res) => {
            if (res?.success == true && res?.data) {
                let new_setting = Object.assign({}, props?.wdkit_meta, { 'Setting': FinalData });
                setdisableswitch(false);
                props.wdkit_set_meta(new_setting);

            }
        })
    }

